$(document).ready(function(){$(".hamburger").on("click",function(){$("#navigation").toggleClass("open")});const e=`<ul id="main-nav"><li><a href="areas-of-practice.html">Areas of practice</a></li><li><a href="ip-showcase.html">IP showcase</a></li><li><a href="accolades.html">Accolades</a></li><li><a href="publications.html">Publications</a></li><li><a href="about-us.html">About Us</a></li><li><a href="contact-us.html">Contact</a></li></ul>`;$("#navigation").append(e);let n=window.location.pathname.split("/");for(let e=0;e<n.length;e++)if(n[e].includes(".html")){$("#main-nav li").each(function(){let t=$(this).find("a").attr("href");t===n[e]&&$(this).addClass("selected")})}});